INSERT INTO public.directus_operations (id, name, key, type, position_x, position_y, options, resolve, reject, flow, date_created, user_created) VALUES ('cea52e13-67bf-4568-b37b-2e16182c45c6', 'Run Script', 'activities_populated', 'exec', 75, 17, '{"code":"module.exports = async function (data) {\n  return data.activities.map((a) => {\n    reg = data.activity_registrations.find((r) => r.icbd_activity === a.id);\n    slot = a.timeslots.find((t) => t.start_time === reg.start);\n    trans = data.translations\n      .filter((t) => t.languages_code === \"en-US\")\n      .find((t) => a.translations.indexOf(t.id) !== -1);\n    return { ...a, translation: trans, timeslot: slot };\n  }).sort((a, b) => a.timeslot.start_time.localeCompare(b.timeslot.start_time));\n};\n"}', 'c1b1cf38-94f9-4f40-a26e-3abef5db56c9', NULL, '2eb347c0-164e-4a8b-a6f1-e4d66472389b', '2025-02-18 19:38:06.617+00', '9d7b9e12-2de6-4aa6-85e3-6631768b0309');
